DROP TABLE IF EXISTS `novel_info`;
CREATE TABLE `novel_info`  (
  `id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT 'id，主键id',
  `cover_url` varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '封面图片绝对路径URL',
  `title` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '标题，小说的名称。',
  `author` varchar(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '作者',
  `publisher` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '出版社或爬取的网站名称。',
  `total_words` decimal(16, 0) UNSIGNED NULL DEFAULT 0 COMMENT '总字数。',
  `novel_status` tinyint(4) UNSIGNED NOT NULL DEFAULT 0 COMMENT '状态，0-已完结，1-连载中。',
  `category_type` smallint(4) UNSIGNED NOT NULL COMMENT '类目类型编号。',
  `category_cn_text` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '类目类型中文名称。',
  `category_en_text` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '类目类型英文名称。',
  `introduction` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT '小说简介',
  `readers` decimal(16, 0) UNSIGNED NULL DEFAULT 0 COMMENT '阅读（点击）用户数；默认“0-阅读（点击）用户数”',
  `recent_readers` decimal(16, 0) UNSIGNED NULL DEFAULT 0 COMMENT '最近跟随阅读（点击）用户数；默认“0-最近跟随阅读（点击）用户数”',
  `click_number` decimal(16, 0) UNSIGNED NULL DEFAULT 0 COMMENT '点击次数',
  `newest_chapter_id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '最新章节id',
  `newest_chapter_title` varchar(512) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '最新章节标题（名称）',
  `total_chapters` int(6) UNSIGNED NULL DEFAULT 0 COMMENT '章节总数',
  `retention` tinyint(3) UNSIGNED NULL DEFAULT 0 COMMENT '留存率，现在只是保存数字，显示的时候加上百分比',
  `create_time` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '创建时间',
  `update_time` timestamp(0) NOT NULL DEFAULT CURRENT_TIMESTAMP(0) ON UPDATE CURRENT_TIMESTAMP(0) COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `idx_title`(`title`, `author`, `category_type`, `category_cn_text`) USING BTREE COMMENT '保证小说的名称，小说的作者，小说的中文分类名称，小说的分类类型type唯一的索引。'
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '小说信息记录表' ROW_FORMAT = Dynamic;

