INSERT INTO `novel_info` VALUES ('37bf32a8fb4c4148bdc1581f1101d67b', 'https://fm.88dush.com/108/108241/108241s.jpg', '终极美女保镖', '魂断心不死', '八八读书网（88dush.com）', 0, 1, 207, '历史穿越', 'chuangyuechongsheng', '有恩，砸锅卖铁还你；有仇，拆房卖地干你。杨天的宗旨就是有恩必还，有仇必报。为兄弟，为红颜，为正义，拔刀再现江湖，一代军神化身美女保镖，演绎热血传奇人生。', 0, 0, 0, 'd79a6cf466d44595b61b3a0748db5630', '第2477章 不一样的林啸天', 2477, 0, '2019-04-27 23:20:53', '2019-04-28 07:11:51');
